mapper.select:
SELECT brd_title, brd_writer, brd_wt_time, brd_lookup, brd_content
FROM board
WHERE brd_key = #{key}

mapper.update:
UPDATE board
SET brd_lookup = brd_lookup + 1
WHERE brd_key = #{key}

UPDATE board
SET del_flag = 1
WHERE brd_key = #{key}

mapper.select:
SELECT brd_key, brd_title, brd_writer, brd_wt_time, brd_lookup
FROM board
WHERE del_flag = 0 and brd_title = #{keyword} or brd_content = #{keyword}
